package week1.day2;

public class NumberChecking {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int number=45;

for (int i=0; i<=number; i++)
{
if(i%2==1)
{
	System.out.println(i + "odd no");
}
else
{
	System.out.println(i + " Even no");
}
}
	}
}

