package Week2.day1;

public class RemoveDuplicates {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name="We learn java basics as part of java sessions in java week";
		int count=0;
		String[] split=name.split("java");
		for(int i=0;i<=count;i++)
		{
				for(int j=i+1;j<=i+1;j++)
				{
					if(split[i]!=split[j])
						count=i+1;
					{
		System.out.println("remove duplicate no is:"+split[i]);
					}
				}
			
			

			}

	}
}

